CREATE VIEW VW_01_PROYECTOSEJECUCIONXCIUDAD (NomCiudad, CodConvocatoria, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T5.Id_Proyecto AND T4.Id_Institucion=T5.CodInstitucion AND T2.Viable=1 AND T5.CodEstado>=7