CREATE VIEW VW_02_Desempeo_EvaluadorXConv (Nombres, Apellidos, CodInstitucion, CodConvocatoria, Puntaje, Id_Item, NomItem, CodTabEvaluacion, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.CodInstitucion, T2.CodConvocatoria, T2.Puntaje, T3.Id_Item, T3.NomItem, T3.CodTabEvaluacion, T4.Id_Proyecto, T4.NomProyecto FROM Contacto T1, EvaluacionEvaluador T2, Item T3, Proyecto T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Item=T2.CodItem AND T5.CodProyecto=T4.Id_Proyecto AND T5.CodRol=4