CREATE VIEW VW_05REP_LEGALXRAZSOCIAL (Nombres, Apellidos, Identificacion, razonsocial, Nit, RepresentanteLegal, Id_Proyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T3.razonsocial, T3.Nit, T4.RepresentanteLegal, T5.Id_Proyecto FROM Contacto T1, ConvocatoriaProyecto T2, Empresa T3, EmpresaContacto T4, Proyecto T5 WHERE T1.Id_Contacto=T4.codcontacto AND T2.CodProyecto=T5.Id_Proyecto AND T3.codproyecto=T5.Id_Proyecto AND T3.id_empresa=T4.codempresa AND T5.CodEstado=7