CREATE VIEW VW_1000 (NomCiudad, NomDepartamento, NomUnidad, Id_Proyecto, NomProyecto, FechaCreacion, FechaModificacion, Fecha, CodConvocatoria) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T4.FechaCreacion, T4.FechaModificacion, T5.Fecha, T5.CodConvocatoria FROM Ciudad T1, departamento T2, Institucion T3, Proyecto T4, ProyectoFormalizacion T5 WHERE T1.CodDepartamento=T2.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T3.Id_Institucion=T4.CodInstitucion AND T4.Id_Proyecto=T5.codProyecto AND T4.CodEstado=2