CREATE VIEW VW_2CONV_DATOS (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, razonsocial, T_Empresa, M_Empresa, Nit, Id_Proyecto, NomProyecto, CodRol) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T4.razonsocial, T4.Telefono, T4.Email, T4.Nit, T5.Id_Proyecto, T5.NomProyecto, T6.CodRol FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, Empresa T4, Proyecto T5, ProyectoContacto T6 WHERE T1.Id_Ciudad=T2.CodCiudad AND T1.Id_Ciudad=T4.CodCiudad AND T1.Id_Ciudad=T5.CodCiudad AND T2.Id_Contacto=T6.CodContacto AND T3.CodProyecto=T5.Id_Proyecto AND T6.CodProyecto=T5.Id_Proyecto AND T4.codproyecto=T5.Id_Proyecto AND T3.CodConvocatoria=2 AND T6.CodRol<=3