CREATE VIEW VW_50 (Nombres, Apellidos, Identificacion, Email, Fecha, ValorRecomendado, Id_Proyecto, NomProyecto, FechaCreacion, CodRol, Inactivo) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T2.Fecha, T3.ValorRecomendado, T4.Id_Proyecto, T4.NomProyecto, T4.FechaCreacion, T5.CodRol, T5.Inactivo FROM Contacto T1, ConvocatoriaProyecto T2, EvaluacionObservacion T3, Proyecto T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.CodProyecto=T4.Id_Proyecto AND T3.CodProyecto=T4.Id_Proyecto AND T5.CodProyecto=T4.Id_Proyecto AND T4.Id_Proyecto=8578 AND T5.Inactivo=0