CREATE VIEW VW_ACREDITACION_ACTA_X_CONV (Id_Convocatoria, NomConvocatoria, Id_Proyecto, NomProyecto, Fecha, NomSector, NomSubSector) AS SELECT T1.Id_Convocatoria, T1.NomConvocatoria, T3.Id_Proyecto, T3.NomProyecto, T4.Fecha, T5.NomSector, T6.NomSubSector FROM Convocatoria T1, ConvocatoriaProyecto T2, Proyecto T3, ProyectoAcreditacion T4, Sector T5, SubSector T6 WHERE T5.Id_Sector=T6.CodSector AND T3.CodSubSector=T6.Id_SubSector AND T3.Id_Proyecto=T4.CodProyecto AND T4.CodConvocatoria=T1.Id_Convocatoria AND T3.Id_Proyecto=T2.CodProyecto AND T1.Id_Convocatoria=T2.CodConvocatoria AND T1.Id_Convocatoria=112