CREATE VIEW VW_ACREDITACION_CON_CONCEPTO (NomCiudad, NomDepartamento, Id_Proyecto, NomProyecto, ObservacionFinal, Fecha, EstadoAcreditacion, NomConvocatoria) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.Id_Proyecto, T3.NomProyecto, T4.ObservacionFinal, T4.Fecha, T5.EstadoAcreditacion, T6.NomConvocatoria FROM Ciudad T1, departamento T2, Proyecto T3, ProyectoAcreditacion T4, EstadoProyectoAcreditacion T5, Convocatoria T6 WHERE T3.Id_Proyecto=T4.CodProyecto AND T4.CodProyecto=T5.Id_Proyecto AND T5.CodConvocatoria=T6.Id_Convocatoria AND T3.CodCiudad=T1.Id_Ciudad AND T1.CodDepartamento=T2.Id_Departamento AND T6.Id_Convocatoria=189 AND T6.Id_Convocatoria=206