CREATE VIEW VW_ACREDITADORES (Nombres, Apellidos, Id_Proyecto, NomProyecto, FechaInicio, FechaFin, Acreditador) AS SELECT T1.Nombres, T1.Apellidos, T2.Id_Proyecto, T2.NomProyecto, T3.FechaInicio, T3.FechaFin, T3.Acreditador FROM Contacto T1, Proyecto T2, ProyectoContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Proyecto=T3.CodProyecto AND T3.CodRol=9 AND T2.Id_Proyecto=51533