CREATE VIEW VW_ACTUALIZACION_TABS_PROYECTOS (Id_Contacto, Nombres, Apellidos, Id_Proyecto, NomProyecto, NomTab, FechaModificacion) AS SELECT T1.Id_Contacto, T1.Nombres, T1.Apellidos, T2.Id_Proyecto, T2.NomProyecto, T3.NomTab, T4.FechaModificacion FROM Contacto T1, Proyecto T2, Tab T3, TabProyecto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T2.Id_Proyecto=T4.CodProyecto AND T3.Id_Tab=T4.CodTab AND T2.Id_Proyecto=35926