CREATE VIEW VW_AF_ACREDITACION (Id_Proyecto, NomProyecto, Nombres, Apellidos, CodConvocatoria, EstadoAcreditacion, EstadoProyecto, ObservacionFinal, Fecha) AS SELECT T1.Id_Proyecto, T1.NomProyecto, T1.Nombres, T1.Apellidos, T1.CodConvocatoria, T1.EstadoAcreditacion, T1.EstadoProyecto, T2.ObservacionFinal, T2.Fecha FROM EstadoProyectoAcreditacion T1, ProyectoAcreditacionUltimo T2 WHERE T1.Id_Proyecto=T2.CodProyecto AND T1.CodConvocatoria=T2.CodConvocatoria AND T1.CodEstado=T2.CodEstado AND T1.CodConvocatoria>=133