CREATE VIEW VW_AF_ACREDITADOR_X_PROYECTO (Nombres, Apellidos, NomEstado, Id_Proyecto, NomProyecto, FechaFin) AS SELECT T1.Nombres, T1.Apellidos, T2.NomEstado, T3.Id_Proyecto, T3.NomProyecto, T4.FechaFin FROM Contacto T1, Estado T2, Proyecto T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T3.Id_Proyecto=T4.CodProyecto AND T2.Id_Estado=T3.CodEstado AND T4.CodRol=9 AND T4.CodConvocatoria=116