CREATE VIEW VW_AF_EMPRENDEDORESXCIUDAD (NomCiudad, NomDepartamento, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, Id_Proyecto, NomProyecto, FechaCreacion, FechaModificacion, FechaDesactivacion, MotivoDesactivacion, CodEstado, CodRol, CodConvocatoria) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.Nombres, T3.Apellidos, T3.Identificacion, T3.Email, T3.Direccion, T3.Telefono, T4.Id_Proyecto, T4.NomProyecto, T4.FechaCreacion, T4.FechaModificacion, T4.FechaDesactivacion, T4.MotivoDesactivacion, T4.CodEstado, T5.CodRol, T5.CodConvocatoria FROM Ciudad T1, departamento T2, Contacto T3, Proyecto T4, ProyectoContacto T5 WHERE T1.CodDepartamento=T2.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T3.Id_Contacto=T5.CodContacto AND T4.Id_Proyecto=T5.CodProyecto AND T5.Inactivo=0 AND T1.CodDepartamento=76 AND T5.CodRol=3