CREATE VIEW VW_AF_GESTORES_EXT_PLANES_FORMALIZADOS (NomInstitucion, NomUnidad, CodProyecto, CodContacto, CodRol, CodConvocatoria) AS SELECT T1.NomInstitucion, T1.NomUnidad, T3.CodProyecto, T3.CodContacto, T3.CodRol, T4.CodConvocatoria FROM Institucion T1, InstitucionContacto T2, ProyectoContacto T3, ProyectoFormalizacion T4 WHERE T1.Id_Institucion=T2.CodInstitucion AND T2.CodContacto=T3.CodContacto AND T1.CodTipoInstitucion=3 AND T3.CodRol<=2 AND T3.CodProyecto=T4.codProyecto