CREATE VIEW VW_AF_REPORTE_ACREDITACION (Id_ProyectoAcreditacion, CodProyecto, CodConvocatoria, ObservacionFinal, Fecha, CodEstado, Nombres, Apellidos, CodRol, FechaInicio, FechaFin, Acreditador) AS SELECT T1.Id_ProyectoAcreditacion, T1.CodProyecto, T1.CodConvocatoria, T1.ObservacionFinal, T1.Fecha, T1.CodEstado, T2.Nombres, T2.Apellidos, T3.CodRol, T3.FechaInicio, T3.FechaFin, T3.Acreditador FROM ProyectoAcreditacionUltimo T1, Contacto T2, ProyectoContacto T3 WHERE T1.CodProyecto=T3.CodProyecto AND T1.CodConvocatoria=T3.CodConvocatoria AND T2.Id_Contacto=T3.CodContacto AND T1.CodConvocatoria>=126 AND T3.CodRol=9 AND T3.Acreditador=1