CREATE VIEW VW_APRENDIZ_BASICA (NomCiudad, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha, CodConvocatoria, NomConvocatoria, NomSector, NomSubSector, Nombres, Apellidos, Identificacion, Genero, FechaNacimiento, Cargo, Email, Direccion, Telefono, Inactivo, CodTipoAprendiz) AS SELECT T1.NomCiudad, T1.NomDepartamento, T1.NomInstitucion, T1.NomUnidad, T1.Id_Proyecto, T1.NomProyecto, T1.Recursos, T1.Fecha, T1.CodConvocatoria, T1.NomConvocatoria, T1.NomSector, T1.NomSubSector, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Genero, T2.FechaNacimiento, T2.Cargo, T2.Email, T2.Direccion, T2.Telefono, T2.Inactivo, T2.CodTipoAprendiz FROM Conv_10_Avalados T1, Contacto T2, ProyectoContacto T3 WHERE T2.Id_Contacto=T3.CodContacto AND T1.CodConvocatoria=105 AND T1.Id_Proyecto=T3.CodProyecto