CREATE VIEW VW_AVALADOSCONV6 (NomCiudad, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Fecha) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T6.Fecha FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoFormalizacion T6 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T5.Id_Proyecto AND T6.codProyecto=T5.Id_Proyecto AND T4.Id_Institucion=T5.CodInstitucion AND T2.CodConvocatoria=46 AND T6.CodConvocatoria=46