CREATE VIEW VW_AVALADOSREGIONALESCIERRE6AGOSTO (NomCiudad, Nombres, Apellidos, Identificacion, Email, Direccion, Telefono, NomConvocatoria, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodRol, Recursos, Fecha, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Identificacion, T2.Email, T2.Direccion, T2.Telefono, T3.NomConvocatoria, T5.NomDepartamento, T6.NomInstitucion, T6.NomUnidad, T7.Id_Proyecto, T7.NomProyecto, T8.CodRol, T9.Recursos, T10.Fecha, T11.NomSector, T12.NomSubSector FROM Ciudad T1, Contacto T2, Convocatoria T3, ConvocatoriaProyecto T4, departamento T5, Institucion T6, Proyecto T7, ProyectoContacto T8, ProyectoFinanzasIngresos T9, ProyectoFormalizacion T10, Sector T11, SubSector T12 WHERE T1.Id_Ciudad=T7.CodCiudad AND T1.CodDepartamento=T5.Id_Departamento AND T2.Id_Contacto=T8.CodContacto AND T3.Id_Convocatoria=T4.CodConvocatoria AND T6.Id_Institucion=T7.CodInstitucion AND T8.CodProyecto=T7.Id_Proyecto AND T9.CodProyecto=T7.Id_Proyecto AND T10.codProyecto=T7.Id_Proyecto AND T12.Id_SubSector=T7.CodSubSector AND T12.CodSector=T11.Id_Sector AND T4.CodProyecto=T7.Id_Proyecto AND T4.CodConvocatoria>=24 AND T10.CodConvocatoria>=24