CREATE VIEW VW_AVALADOSTIBU1 (NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Recursos, Fecha, NomSector, NomSubSector) AS SELECT T2.NomInstitucion, T2.NomUnidad, T3.Id_Proyecto, T3.NomProyecto, T4.Recursos, T5.Fecha, T6.NomSector, T7.NomSubSector FROM ConvocatoriaProyecto T1, Institucion T2, Proyecto T3, ProyectoFinanzasIngresos T4, ProyectoFormalizacion T5, Sector T6, SubSector T7 WHERE T1.CodProyecto=T3.Id_Proyecto AND T2.Id_Institucion=T3.CodInstitucion AND T4.CodProyecto=T3.Id_Proyecto AND T5.codProyecto=T3.Id_Proyecto AND T7.Id_SubSector=T3.CodSubSector AND T6.Id_Sector=T7.CodSector AND T1.CodConvocatoria=50 AND T5.CodConvocatoria=50