CREATE VIEW VW_AVALADOS_X_SECTOR (NomCiudad, Nombres, Apellidos, Email, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, CodEstado, CodRol, Fecha, CodConvocatoria, NomSector) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T3.NomInstitucion, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T4.CodEstado, T5.CodRol, T6.Fecha, T6.CodConvocatoria, T7.NomSector FROM Ciudad T1, Contacto T2, Institucion T3, Proyecto T4, ProyectoContacto T5, ProyectoFormalizacion T6, Sector T7, SubSector T8 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Contacto=T5.CodContacto AND T3.Id_Institucion=T4.CodInstitucion AND T4.Id_Proyecto=T5.CodProyecto AND T4.Id_Proyecto=T6.codProyecto AND T4.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T6.CodConvocatoria=4 AND T5.CodRol<=3 AND T4.CodEstado=3