CREATE VIEW VW_AVALADO_TIBU (NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Fecha, NomSector, NomSubSector) AS SELECT T3.NomInstitucion, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.Fecha, T6.NomSector, T7.NomSubSector FROM Ciudad T1, ConvocatoriaProyecto T2, Institucion T3, Proyecto T4, ProyectoFormalizacion T5, Sector T6, SubSector T7 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Institucion=T4.CodInstitucion AND T5.codProyecto=T4.Id_Proyecto AND T7.Id_SubSector=T4.CodSubSector AND T7.CodSector=T6.Id_Sector AND T1.Id_Ciudad=54810 AND T2.CodConvocatoria=13 AND T5.CodConvocatoria=13