CREATE VIEW VW_BASE_CL (Nombres, Apellidos, Identificacion, Email, Clave, Id_Proyecto, NomProyecto) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T1.Clave, T2.Id_Proyecto, T2.NomProyecto FROM Contacto T1, Proyecto T2, ProyectoContacto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.Id_Proyecto=T3.CodProyecto