CREATE VIEW VW_C1_CONS_SINDICATO (encargofiduciario, razonsocial, DESEMBOLSADO, Estado, PRIMERA_FECHA, Id_Proyecto, NomProyecto) AS SELECT T1.encargofiduciario, T3.razonsocial, Sum(T4.CantidadDinero), T4.Estado, Min(T4.FechaCoordinador), T5.Id_Proyecto, T5.NomProyecto FROM Convocatoria T1, ConvocatoriaProyecto T2, Empresa T3, PagoActividad T4, Proyecto T5 WHERE T1.Id_Convocatoria=T2.CodConvocatoria AND T2.CodProyecto=T3.codproyecto AND T3.codproyecto=T5.Id_Proyecto AND T4.CodProyecto=T3.codproyecto AND T4.Estado=4 GROUP BY T1.encargofiduciario, T3.razonsocial, T4.Estado, T5.Id_Proyecto, T5.NomProyecto