CREATE VIEW VW_C1_DATOSVARIOS (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, NomDepartamento, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto, Sumario) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T3.NomDepartamento, T4.NomInstitucion, T4.NomUnidad, T5.Id_Proyecto, T5.NomProyecto, T5.Sumario FROM Ciudad T1, Contacto T2, departamento T3, Institucion T4, Proyecto T5, ProyectoContacto T6 WHERE T1.Id_Ciudad=T2.CodCiudad AND T1.CodDepartamento=T3.Id_Departamento AND T2.Id_Contacto=T6.CodContacto AND T4.Id_Institucion=T5.CodInstitucion AND T6.CodProyecto=T5.Id_Proyecto AND T6.CodRol=3 AND T6.Inactivo=0 AND T5.Id_Proyecto=1789