CREATE VIEW VW_C1_DATOSVARIOS_MOD1 (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, NomDepartamento, razonsocial, ObjetoSocial, NomInstitucion, NomUnidad, Id_Proyecto, NomProyecto) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T4.NomDepartamento, T5.razonsocial, T5.ObjetoSocial, T7.NomInstitucion, T7.NomUnidad, T8.Id_Proyecto, T8.NomProyecto FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Empresa T5, EmpresaContacto T6, Institucion T7, Proyecto T8 WHERE T1.Id_Ciudad=T2.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Contacto=T6.codcontacto AND T3.CodProyecto=T8.Id_Proyecto AND T5.codproyecto=T8.Id_Proyecto AND T5.id_empresa=T6.codempresa AND T7.Id_Institucion=T8.CodInstitucion AND T3.CodConvocatoria=1 AND T3.Viable=1 AND T6.RepresentanteLegal=1