CREATE VIEW VW_C1_EMPRENDEDORES2 (CodEstado, Contacto, Rol, CodigoProyecto, NombreProyecto, CodConvocatoria, Viable) AS SELECT T1.CodEstado, T2.Contacto, T2.Rol, T2.CodigoProyecto, T2.NombreProyecto, T3.CodConvocatoria, T3.Viable FROM Proyecto T1, ContactosRol T2, ConvocatoriaProyecto T3 WHERE T2.CodigoProyecto=T3.CodProyecto AND T1.CodContacto=T2.id_Contacto AND T3.CodConvocatoria=1 AND T1.CodEstado=5