CREATE VIEW VW_C1_EMPRESAPROYECTO (Nombres, Apellidos, Email, NomDepartamento, razonsocial, NomUnidad, Nopagos, ValorPagado, Id_Proyecto, NomProyecto) AS SELECT T2.Nombres, T2.Apellidos, T2.Email, T4.NomDepartamento, T5.razonsocial, T6.NomUnidad, Count(T7.Id_PagoActividad), Sum(T7.CantidadDinero), T8.Id_Proyecto, T8.NomProyecto FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Empresa T5, Institucion T6, PagoActividad T7, Proyecto T8, ProyectoContacto T9 WHERE T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Contacto=T9.CodContacto AND T9.CodProyecto=T8.Id_Proyecto AND T8.Id_Proyecto=T5.codproyecto AND T6.Id_Institucion=T8.CodInstitucion AND T5.codproyecto=T7.CodProyecto AND T1.Id_Ciudad=T5.CodCiudad AND T3.CodProyecto=T8.Id_Proyecto AND T3.CodConvocatoria=1 AND T9.CodRol=1 AND T7.Estado=4 GROUP BY T2.Nombres, T2.Apellidos, T2.Email, T4.NomDepartamento, T5.razonsocial, T6.NomUnidad, T8.Id_Proyecto, T8.NomProyecto