CREATE VIEW VW_C1_EVALUADOR_POR_PROYECTO (Nombres, Apellidos, Identificacion, Viable, Id_Proyecto, NomProyecto, CodRol, CodConvocatoria) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T2.Viable, T3.Id_Proyecto, T3.NomProyecto, T4.CodRol, T4.CodConvocatoria FROM Contacto T1, ConvocatoriaProyecto T2, Proyecto T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.CodContacto AND T2.CodProyecto=T4.CodProyecto AND T3.Id_Proyecto=T4.CodProyecto AND T4.CodRol=4 AND T4.CodConvocatoria=1 AND T2.CodConvocatoria=1