CREATE VIEW VW_C1_FECHA_NACIMIENTO (Nombres, Apellidos, Identificacion, FechaNacimiento, CodConvocatoria, Id_Proyecto, NomProyecto, CodEstado) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.FechaNacimiento, T2.CodConvocatoria, T3.Id_Proyecto, T3.NomProyecto, T3.CodEstado FROM Contacto T1, ConvocatoriaProyecto T2, Proyecto T3 WHERE T1.Id_Contacto=T3.CodContacto AND T2.CodProyecto=T3.Id_Proyecto AND T3.CodEstado=5 AND T2.CodConvocatoria=1