CREATE VIEW VW_C1_NO_VIABLES (NomDepartamento, Id_Proyecto, NomProyecto, NomSector) AS SELECT T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T5.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4, Sector T5, SubSector T6 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.CodProyecto=T4.Id_Proyecto AND T3.Id_Departamento=T1.CodDepartamento AND T4.CodSubSector=T6.Id_SubSector AND T5.Id_Sector=T6.CodSector AND T2.CodConvocatoria=1 AND T4.Id_Proyecto=3087