CREATE VIEW VW_C1_PLANESXSECTOR (CodConvocatoria, Id_Proyecto, NomProyecto, NomSector, NomSubSector) AS SELECT T1.CodConvocatoria, T2.Id_Proyecto, T2.NomProyecto, T3.NomSector, T4.NomSubSector FROM ConvocatoriaProyecto T1, Proyecto T2, Sector T3, SubSector T4 WHERE T1.CodProyecto=T2.Id_Proyecto AND T2.CodSubSector=T4.Id_SubSector AND T3.Id_Sector=T4.CodSector AND T1.CodConvocatoria=1