CREATE VIEW VW_C2EMPLEOSGENERADOS (Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, Id_Departamento, NomDepartamento, Id_Estado, Id_Proyecto, NomProyecto) AS SELECT T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T4.Id_Departamento, T4.NomDepartamento, T5.Id_Estado, T6.Id_Proyecto, T6.NomProyecto FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Estado T5, Proyecto T6 WHERE T1.CodProyecto=T6.Id_Proyecto AND T5.Id_Estado=T6.CodEstado AND T2.CodDepartamento=T4.Id_Departamento AND T2.Id_Ciudad=T6.CodCiudad AND T3.CodProyecto=T6.Id_Proyecto AND T3.CodConvocatoria=2