CREATE VIEW VW_C2_APTROBADOS_IDH (Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomCiudad, IDH, Viable, NomDepartamento, Id_Proyecto, NomProyecto, TiempoProyeccion, NomSector, NomSubSector) AS SELECT T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T2.NomCiudad, T2.IDH, T3.Viable, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto, T6.TiempoProyeccion, T7.NomSector, T8.NomSubSector FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5, ProyectoMercadoProyeccionVentas T6, Sector T7, SubSector T8 WHERE T1.CodProyecto=T5.Id_Proyecto AND T2.CodDepartamento=T4.Id_Departamento AND T3.CodProyecto=T5.Id_Proyecto AND T2.Id_Ciudad=T5.CodCiudad AND T5.Id_Proyecto=T6.CodProyecto AND T5.CodSubSector=T8.Id_SubSector AND T7.Id_Sector=T8.CodSector AND T3.CodConvocatoria=2 AND T3.Viable=1