CREATE VIEW VW_C2_AVALADOS (Ciudad, Departamento, Unidad, Id_Proyecto, Proyecto, Valor_Solicitado) AS SELECT T1.NomCiudad, T2.NomDepartamento, T3.NomUnidad, T4.Id_Proyecto, T4.NomProyecto, T5.Recursos FROM Ciudad T1, departamento T2, Institucion T3, Proyecto T4, ProyectoFinanzasIngresos T5 WHERE T1.CodDepartamento=T2.Id_Departamento AND T4.CodCiudad=T1.Id_Ciudad AND T4.CodInstitucion=T3.Id_Institucion AND T4.Id_Proyecto=T5.CodProyecto AND T4.CodEstado=3