CREATE VIEW VW_C2_AVAL_VALSOL (Ciudad, Convocatoria, Departamento, Estado, NombreEstado, Unidad, Id_Proyecto, Proyecto, CodInstitucion, Valo_Solicitado, TipoInstitucion) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.Id_Estado, T4.NomEstado, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T6.CodInstitucion, T7.Recursos, T8.NomTipoInstitucion FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Estado T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7, TipoInstitucion T8 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T6.CodCiudad AND T6.CodEstado=T4.Id_Estado AND T6.CodInstitucion=T5.Id_Institucion AND T6.Id_Proyecto=T7.CodProyecto AND T5.CodTipoInstitucion=T8.Id_TipoInstitucion AND T2.CodProyecto=T6.Id_Proyecto AND T2.CodConvocatoria=2 AND T8.Id_TipoInstitucion<>2