CREATE VIEW VW_C2_CONSULTA_XRA_PRIORIZACION (CodProyecto, Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomCiudad, IDH, Viable, NomDepartamento, Id_Proyecto, NomProyecto, NomSector, NomSubSector) AS SELECT T1.CodProyecto, T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T2.NomCiudad, T2.IDH, T3.Viable, T4.NomDepartamento, T5.Id_Proyecto, T5.NomProyecto, T6.NomSector, T7.NomSubSector FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Proyecto T5, Sector T6, SubSector T7 WHERE T1.CodProyecto=T3.CodProyecto AND T5.Id_Proyecto=T3.CodProyecto AND T2.CodDepartamento=T4.Id_Departamento AND T2.Id_Ciudad=T5.CodCiudad AND T5.CodSubSector=T7.Id_SubSector AND T6.Id_Sector=T7.CodSector AND T3.CodConvocatoria=2 AND T3.Viable=1