CREATE VIEW VW_C2_DATOSVARIOS (NomCiudad, Nombres, Apellidos, Email, Direccion, Telefono, NomDepartamento, razonsocial, ObjetoSocial) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T2.Email, T2.Direccion, T2.Telefono, T4.NomDepartamento, T5.razonsocial, T5.ObjetoSocial FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, departamento T4, Empresa T5, EmpresaContacto T6, Institucion T7, Proyecto T8 WHERE T1.Id_Ciudad=T5.CodCiudad AND T1.CodDepartamento=T4.Id_Departamento AND T2.Id_Contacto=T6.codcontacto AND T3.CodProyecto=T8.Id_Proyecto AND T5.codproyecto=T8.Id_Proyecto AND T5.id_empresa=T6.codempresa AND T7.Id_Institucion=T8.CodInstitucion AND T3.CodConvocatoria=2 AND T3.Viable=1 AND T6.RepresentanteLegal=1