CREATE VIEW VW_C2_DAT_EMPRENDEDORES (NomCiudad, Nombres, Apellidos, Id_Proyecto, NomProyecto, Nombre) AS SELECT T1.NomCiudad, T2.Nombres, T2.Apellidos, T4.Id_Proyecto, T4.NomProyecto, T6.Nombre FROM Ciudad T1, Contacto T2, ConvocatoriaProyecto T3, Proyecto T4, ProyectoContacto T5, Rol T6 WHERE T1.Id_Ciudad=T4.CodCiudad AND T2.Id_Contacto=T5.CodContacto AND T5.CodProyecto=T4.Id_Proyecto AND T3.CodProyecto=T4.Id_Proyecto AND T6.Id_Rol=T5.CodRol AND T3.Viable=1