CREATE VIEW VW_C2_DESEMPENO (Nombres, Apellidos, Identificacion, Email, CodProyecto, Puntaje, NomItem, Texto, p1) AS SELECT T1.Nombres, T1.Apellidos, T1.Identificacion, T1.Email, T2.CodProyecto, T2.Puntaje, T3.NomItem, T4.Texto, T4.Puntaje FROM Contacto T1, EvaluacionEvaluador T2, Item T3, ItemEscala T4, ProyectoContacto T5 WHERE T1.Id_Contacto=T5.CodContacto AND T2.CodProyecto=T5.CodProyecto AND T3.Id_Item=T2.CodItem AND T3.Id_Item=T4.CodItem AND T2.CodConvocatoria=2 AND T5.CodConvocatoria=2 AND T5.CodRol=4