CREATE VIEW VW_C2_DESEMPENO1 (Nombres, Apellidos, CodProyecto, CodConvocatoria, Puntaje, NomItem) AS SELECT T1.Nombres, T1.Apellidos, T2.CodProyecto, T2.CodConvocatoria, T2.Puntaje, T3.NomItem FROM Contacto T1, EvaluacionEvaluador T2, Item T3, ProyectoContacto T4 WHERE T1.Id_Contacto=T4.Id_ProyectoContacto AND T2.CodProyecto=T4.CodProyecto AND T2.CodItem=T3.Id_Item AND T4.CodRol=4 AND T2.CodConvocatoria=2