
CREATE VIEW VW_C2_EMPLEOS_GENERADOS (Total, Generado, Joven, Desplazado, Madre, Minoria, Recluido, Desmovilizado, Discapacitado, Desvinculado, NomCiudad, CodConvocatoria, NomDepartamento, Id_Estado, Id_Proyecto, NomProyecto) AS SELECT T1.Total, T1.Generado, T1.Joven, T1.Desplazado, T1.Madre, T1.Minoria, T1.Recluido, T1.Desmovilizado, T1.Discapacitado, T1.Desvinculado, T2.NomCiudad, T3.CodConvocatoria, T4.NomDepartamento, T5.Id_Estado, T6.Id_Proyecto, T6.NomProyecto FROM EmpleosGenerados T1, Ciudad T2, ConvocatoriaProyecto T3, departamento T4, Estado T5, Proyecto T6 WHERE T1.CodProyecto=T6.Id_Proyecto AND T6.CodEstado=T5.Id_Estado AND T2.CodDepartamento=T4.Id_Departamento AND T2.Id_Ciudad=T6.CodCiudad AND T3.CodProyecto=T6.Id_Proyecto AND T3.CodConvocatoria=2