CREATE VIEW VW_C2_MONTOSOLXREGIONAL (NomDepartamento, SalariosMin) AS SELECT T3.NomDepartamento, Sum(T5.Recursos) FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4, ProyectoFinanzasIngresos T5 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T2.CodProyecto=T4.Id_Proyecto AND T2.CodConvocatoria=2 AND T4.Id_Proyecto=T5.CodProyecto GROUP BY T3.NomDepartamento