CREATE VIEW VW_C2_NARINO (NomCiudad, Justificacion, Viable, ValorRecomendado, Id_Proyecto, NomProyecto, Recursos, NomSector, NomSubSector) AS SELECT T1.NomCiudad, T2.Justificacion, T2.Viable, T4.ValorRecomendado, T5.Id_Proyecto, T5.NomProyecto, T6.Recursos, T7.NomSector, T8.NomSubSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Proyecto T5, ProyectoFinanzasIngresos T6, Sector T7, SubSector T8 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T5.CodCiudad AND T2.CodProyecto=T5.Id_Proyecto AND T4.CodProyecto=T5.Id_Proyecto AND T6.CodProyecto=T5.Id_Proyecto AND T8.Id_SubSector=T5.CodSubSector AND T8.CodSector=T7.Id_Sector AND T3.Id_Departamento=52 AND T2.CodConvocatoria=2 AND T4.CodConvocatoria=2