CREATE VIEW VW_C2_NO_VIABLES_ANTIOQUIA (NomCiudad, CodConvocatoria, Justificacion, Viable, NomDepartamento, Id_Proyecto, NomProyecto, CodEstado) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T2.Justificacion, T2.Viable, T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T4.CodEstado FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4 WHERE T1.CodDepartamento=T3.Id_Departamento AND T2.CodProyecto=T4.Id_Proyecto AND T2.CodConvocatoria=2 AND T2.Viable=0 AND T3.Id_Departamento=5 AND T1.Id_Ciudad=T4.CodCiudad