CREATE VIEW VW_C2_PLANES_AVALADOS (NomCiudad, NomDepartamento, NomEstado, NomUnidad, Id_Proyecto, NomProyecto, Sumario, Fecha, Aval, NomTipoInstitucion) AS SELECT T1.NomCiudad, T3.NomDepartamento, T4.NomEstado, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T6.Sumario, T7.Fecha, T7.Aval, T8.NomTipoInstitucion FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Estado T4, Institucion T5, Proyecto T6, ProyectoFormalizacion T7, TipoInstitucion T8 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T6.CodCiudad AND T2.CodProyecto=T6.Id_Proyecto AND T6.CodEstado=T4.Id_Estado AND T6.CodInstitucion=T5.Id_Institucion AND T7.codProyecto=T6.Id_Proyecto AND T5.CodTipoInstitucion=T8.Id_TipoInstitucion AND T6.CodEstado=3 AND T8.Id_TipoInstitucion<>2 AND T2.CodConvocatoria=45