CREATE VIEW VW_C2_PLANES_POR_SECTORES (Ciudad, Convocatoria, Departamento, Id_Proyecto, Proyecto, Sector) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T3.NomDepartamento, T4.Id_Proyecto, T4.NomProyecto, T5.NomSector FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, Proyecto T4, Sector T5, SubSector T6 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T4.CodCiudad AND T2.CodProyecto=T4.Id_Proyecto AND T4.CodSubSector=T6.Id_SubSector AND T5.Id_Sector=T6.CodSector AND T2.CodConvocatoria=2