CREATE VIEW VW_C2_PROYECCIONVENTAS (Valor, encargofiduciario, razonsocial, Id_Proyecto, NomProyecto) AS SELECT Sum(T1.Precio), T2.encargofiduciario, T4.razonsocial, T5.Id_Proyecto, T5.NomProyecto FROM V_ProyectoProductoPrecio T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4, Proyecto T5, ProyectoProducto T6, ProyectoProductoUnidadesVentas T7 WHERE T1.CodProducto=T6.Id_Producto AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T5.Id_Proyecto AND T4.codproyecto=T5.Id_Proyecto AND T6.CodProyecto=T5.Id_Proyecto AND T7.CodProducto=T6.Id_Producto AND T2.encargofiduciario=103937 GROUP BY T2.encargofiduciario, T4.razonsocial, T5.Id_Proyecto, T5.NomProyecto