CREATE VIEW VW_C2_PYTSVIABILIZADOS (CodConvocatoria, CodProyecto, Fecha, Justificacion, Viable, Id_Proyecto, NomProyecto, Sumario, FechaCreacion, FechaModificacion, FechaDesactivacion, MotivoDesactivacion, CodTipoProyecto, CodEstado, CostoTotal, CodCiudad, CodSubSector, CodContacto, CodInstitucion, Inactivo, Latitud, Longitud) AS SELECT * FROM ConvocatoriaProyecto T1, Proyecto T2 WHERE T1.CodProyecto=T2.Id_Proyecto AND T1.CodConvocatoria=2 AND T2.CodEstado=3