CREATE VIEW VW_C2_Produccion (CodTipoFinanciacion, ValorVentas, encargofiduciario, razonsocial, Id_Proyecto, NomProyecto) AS SELECT T1.CodTipoFinanciacion, Sum(T1.Valor), T2.encargofiduciario, T4.razonsocial, T6.Id_Proyecto, T6.NomProyecto FROM AvanceVentasPOMes T1, Convocatoria T2, ConvocatoriaProyecto T3, Empresa T4, InterventorVentas T5, Proyecto T6 WHERE T1.CodProducto=T5.id_ventas AND T2.Id_Convocatoria=T3.CodConvocatoria AND T3.CodProyecto=T6.Id_Proyecto AND T4.codproyecto=T6.Id_Proyecto AND T5.CodProyecto=T6.Id_Proyecto AND T1.CodTipoFinanciacion=2 AND T2.encargofiduciario=103937 GROUP BY T1.CodTipoFinanciacion, T2.encargofiduciario, T4.razonsocial, T6.Id_Proyecto, T6.NomProyecto