CREATE VIEW VW_C2_RESULTADOEVALUACION2 (NomCiudad, CodConvocatoria, Viable, NomDepartamento, ValorRecomendado, NomUnidad, Id_Proyecto, NomProyecto, CodEstado, Recursos) AS SELECT T1.NomCiudad, T2.CodConvocatoria, T2.Viable, T3.NomDepartamento, T4.ValorRecomendado, T5.NomUnidad, T6.Id_Proyecto, T6.NomProyecto, T6.CodEstado, T7.Recursos FROM Ciudad T1, ConvocatoriaProyecto T2, departamento T3, EvaluacionObservacion T4, Institucion T5, Proyecto T6, ProyectoFinanzasIngresos T7 WHERE T1.CodDepartamento=T3.Id_Departamento AND T1.Id_Ciudad=T6.CodCiudad AND T2.CodProyecto=T6.Id_Proyecto AND T4.CodProyecto=T6.Id_Proyecto AND T5.Id_Institucion=T6.CodInstitucion AND T7.CodProyecto=T6.Id_Proyecto AND T2.CodConvocatoria=2 AND T4.CodConvocatoria=2 AND T6.CodEstado=4